import React from "react"
import {
  ComposableMap,
  Geographies,
  Geography,
} from "react-simple-maps"

// url to a valid topojson file
const geoUrl = "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json"

export default function CountryMap({ setTooltipContent }) {
  return (
    <div>
      <ComposableMap data-tip="" data-for="path" projectionConfig={{ scale: 100 }}>
        <Geographies geography={geoUrl}>
          {({ geographies }) => geographies.map(geo =>
            <Geography 
                key={geo.rsmKey} 
                geography={geo} 
                onMouseEnter={() => {
                    console.log(geo.properties);

                    setTooltipContent(`${geo.properties.NAME}<br><br>Confirmed: <br>Recovered: <br>Deaths: `);
                }}
                onMouseLeave={() => {
                    setTooltipContent(``);
                }}
                style={{
                    default: {
                    fill: "#D6D6DA",
                    outline: "none"
                    },
                    hover: {
                    fill: "#F53",
                    outline: "none"
                    },
                    pressed: {
                    fill: "#E42",
                    outline: "none"
                    }
                }} 
            />
          )}
        </Geographies>
      </ComposableMap>      
    </div>
  )
}
