import { useState, useEffect } from "react"

export default function IndexPage() {
    return <Stats></Stats>
}

function Stats() {
    useStats()

    return (
        <div>
            <div>
                <h3>Confirmed: </h3>
            </div>
            <div>
                <h3>Recovered: </h3>
            </div>
            <div>
                <h3>Deaths: </h3>
            </div>
        </div>
    )
}

function useStats() {
    const [stats, setStats] = useState();

    useEffect(() => {
        fetch(`https://covid19.mathdro.id/api/`)
        .then(data => data.json())
        .then(res => {    
            //setStats(res)

            console.log(stats)
            return stats
        })        
    })
}
