import Stats from "../components/Stats";
import CountryMap from "../components/CountryMap";
import { useEffect, useState } from "react";
import ReactTooltip from "react-tooltip";

export default function IndexPage() {
    const[content, setContent] = useState("");
    const [stats, setStats] = useState();

    useEffect(() => {
        fetch(`https://covid19.mathdro.id/api/`)
        .then(data => data.json())
        .then(res => {    
            console.log(res)
            setStats(res)            
        })        
    }, [])
    // empty array as second argument ensures useEffect function only runs once on initial render: https://css-tricks.com/run-useeffect-only-once/

    return (
        <div>
            <Stats stats={stats}></Stats>
            <CountryMap setTooltipContent={setContent}></CountryMap>
            <ReactTooltip html={true} multiline={true}>{content}</ReactTooltip>
        </div>
    )
}
