import React from "react"
import {
  ComposableMap,
  Geographies,
  Geography,
} from "react-simple-maps"

// url to a valid topojson file
const geoUrl = "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json"

export default function CountryMap({ setTooltipContent }) {
    return (
        <div>
        <ComposableMap data-tip="" projectionConfig={{ scale: 75 }} width={300} height={200}>
            <Geographies geography={geoUrl}>
            {({ geographies }) => geographies.map(geo =>
                <Geography 
                    key={geo.rsmKey} 
                    geography={geo} 
                    onMouseEnter={() => {                        
                        setTooltipContent(`${geo.properties.NAME}${getCountryStats(geo.properties.ISO_A2)}`);                        
                    }}
                    onMouseLeave={() => {
                        setTooltipContent(``);
                    }}
                    style={{
                        default: {
                        fill: "#D6D6DA",
                        outline: "none"
                        },
                        hover: {
                        fill: "#F53",
                        outline: "none"
                        },
                        pressed: {
                        fill: "#E42",
                        outline: "none"
                        }
                    }} 
                />
            )}
            </Geographies>
        </ComposableMap>      
        </div>
    )
}

async function getCountryStats(countryCode) {
    const data = await fetch(`https://covid19.mathdro.id/api/countries/${countryCode}`)
    const result = await data.json()

    return `<br><br>Confirmed: ${result.confirmed.value}<br>Recovered: ${result.recovered.value}<br>Deaths: ${result.deaths.value}`
}
