import { useState, useEffect } from "react"

export default function Stats() {
    const [stats, setStats] = useState();

    useEffect(() => {
        fetch(`https://covid19.mathdro.id/api/`)
        .then(data => data.json())
        .then(res => {    
            console.log(res)
            setStats(res)            
        })        
    }, [])
    // empty array as second argument ensures useEffect function only runs once on initial render: https://css-tricks.com/run-useeffect-only-once/ 

    if(!stats) {
        return <p>loading ........</p>
    }

    return (
        <div style={{display: "flex", justifyContent: "space-evenly"}}>
            <div style={styles.statContainer}>
                <h3>Confirmed</h3>
                <p>{stats.confirmed.value.toLocaleString()}</p>
            </div>
            <div>
                <h3>Recovered</h3>
                <p>{stats.recovered.value.toLocaleString()}</p>
            </div>
            <div>
                <h3>Deaths</h3>
                <p>{stats.deaths.value.toLocaleString()}</p>
            </div>
        </div>
    )
}

const styles = {
    statContainer: {
        border: '30px solid green'          
    }
}
